import React, { useCallback, useState } from 'react'

import { useQueryClient } from 'react-query'

import { Box, InputAdornment, InputBase, Typography } from '@mui/material'
import { DataGrid } from '@mui/x-data-grid'
import SearchIcon from '@mui/icons-material/Search'
import { Search } from '../../components'

import debounce from 'lodash.debounce'

export default function Tracks () {
  const queryClient = useQueryClient()
  const queryName = 'trackData'

  const [search, setSearch] = useState('')

  const refetch = () => queryClient.invalidateQueries(queryName)
  // const debouncedTrackSearch = useCallback(debounce(refetch, 400), [])
  const handleTrackSearch = (event) => {
    setSearch(event.target.value)
    // debouncedTrackSearch(event.target.value)
  }

  const columns = [
    { field: 'id', hide: true},
    {
      field: 'title',
      headerName: 'Title'
    },
    { field: 'albumId', hide: true},
    { field: 'mediaTypeId', hide: true},
    { field: 'genreId', hide: true},
    { field: 'composer', hide: true},
    {
      field: 'duration',
      headerName: 'Duration'
    },
    {
      field: 'size',
      headerName: 'Size'
    },
    { field: 'price', hide: true}
  ]

  return (
    <Box display="flex" flexDirection="column" flexGrow={1}>
      <Box my={2}>
        <Typography variant="h3">Tracks</Typography>
      </Box>
      {/* <Search
        value={search || ''}
        onChange={handleTrackSearch}
      /> */}
      <InputBase
        placeholder='Search Tracks'
        variant='outlined'
        fullWidth
        startAdornment={<InputAdornment><SearchIcon/></InputAdornment>}
        value={search || ''}
        onChange={handleTrackSearch}
        sx={{
          backgroundColor: '#F5F7FA',
          border: '1.5px solid #61616150',
          borderRadius: '4px',
          fontWeight: 500,
          paddingLeft: '6px'
        }}
      />
      <DataGrid
        columns={columns}
      />
    </Box>
  )
}
